<?phpinclude '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $todo = mysqli_real_escape_string($conn,$_POST['todo']);    $Query = "INSERT INTO todo_app_tb(todo) values(?)";    $stmt = $conn->prepare($Query);    $stmt->bind_param('s',$todo);    if($stmt->execute()){        echo "Successfully Created New Todo";    }else{        echo "error". mysqli_error($conn);    }}