<?phpinclude  '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $Todo_id = $_POST['Todo_id'];    $Query = "DELETE  FROM todo_app_tb WHERE ID = ?";    //preparing the query    $stmt = $conn->prepare($Query);    //bind param    $stmt->bind_param('i',$Todo_id);    //EXECUTE THE QUERY    if($stmt->execute()){        echo "Successfully Deleted";    }else{        echo "error". mysqli_error($conn);    }}