<?phpinclude "../Database/Connect.php";global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $ID = $_POST['todoId'];    $Query = "SELECT * FROM todo_app_tb WHERE ID = ?";    // preparing the query    $stmt = $conn->prepare($Query);    // bind the parameter or the id    $stmt->bind_param('i',$ID);    $stmt->execute();    // getting the result    $result = $stmt->get_result();    while ($row = mysqli_fetch_assoc($result)){        ?>        <div class="modal fade" tabindex="-1" id="Modal_update">            <div class="modal-dialog modal-dialog-centered modal-sm">                <div class="modal-content">                    <div class="modal-body d-flex flex-column gap-2">                        <div>                            <label id="success_" for="Todo_Save">Update Todo</label>                            <input class="form-control" type="text" id="Todo_Save" value="<?= $row['todo'] ?>">                            <div id="useSaveTodo" class="invalid-feedback"></div>                        </div>                    </div>                    <div class="modal-footer">                      <div id="btn_con" class="bg-danger-subtle p-1 rounded-5">                          <button type="button" class="btn text-danger badge" data-bs-dismiss="modal">                              <i class="fa-solid fa-xmark"></i>                               Cancel                          </button>                      </div>                        <div id="btn_con" class="bg-success-subtle p-1 rounded-5">                            <button id="Save_btn" value="<?= $row['ID'] ?>" type="button" class="btn text-success badge">                                <i class="fa-regular fa-floppy-disk"></i>                                Save                            </button>                        </div>                    </div>                </div>            </div>        </div><?php    }}